package org.data.linking.controller;import com.fasterxml.jackson.core.JsonParseException;import com.fasterxml.jackson.core.JsonProcessingException;import com.fasterxml.jackson.databind.JsonMappingException;import com.fasterxml.jackson.databind.ObjectMapper;import org.data.linking.DataLinkingApplication;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.autoconfigure.web.servlet.AutoConfigureMockMvc;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.ActiveProfiles;import org.springframework.test.web.servlet.MockMvc;import java.io.IOException;@ActiveProfiles("test")@SpringBootTest(classes = DataLinkingApplication.class)@AutoConfigureMockMvcpublic class AbstractTest {    @Autowired    protected MockMvc mvc;    protected String mapToJson(Object obj) throws JsonProcessingException {        ObjectMapper objectMapper = new ObjectMapper();        return objectMapper.writeValueAsString(obj);    }    protected <T> T mapFromJson(String json, Class<T> clazz)            throws JsonParseException, JsonMappingException, IOException {        ObjectMapper objectMapper = new ObjectMapper();        return objectMapper.readValue(json, clazz);    }}